import React, { useEffect, useState } from 'react';
import InputGroup from '../components/InputGroup';
import RowDetails from '../components/RowDetails';
import axios from 'axios';
import Alert from '../components/Alert';




function Rooms() {
  const[rooms,setrooms] =useState([]);
  const [form ,setForm]=useState({});
  const[errors ,setErrors] =useState({});
  const [message, setMessage]= useState("");
  const [show , setShow] = useState(false);

  const onChangeHandler= (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
    
  };
 const onSbmitHandler= (e)=>{
  e.preventDefault();
  axios.post('api/rooms', form)
  .then(res=>{
    setMessage(res.data.message)
    setShow(true)
    setTimeout(() => {
      setShow(false)
    }, 4000);
  })
   .catch(err=>setErrors(err.response.data))
   
 }
 const OnDelete = (id__)=>{
  if(window.confirm("are you sure to delete this rooms")){

    axios.delete(`/api/rooms/${id__}`)
   .then(res=>{
    setMessage(res.data.message)
    setShow(true)
    setTimeout(() => {
      setShow(false)
    }, 4000);
     
   })
  }
}
  useEffect(()=>{
     axios.get('api/rooms')
    .then(res=>{
      setrooms(res.data)
  });
  },[]) 
  return (

    <div>
       <div className="row p-4">
 <Alert message={message} show={show} />
     <div className="mt-4">
        <h2>ROOMS BOOKING</h2>
     </div>
     <div className="col-12 col-lg-4">
         <form onSubmit={onSbmitHandler}>

         <InputGroup
            label="fullname"
            type="text"
            name="fullname"
            onChangeHandler={onChangeHandler}
            errors={errors.fullname}
          
          />
          <InputGroup
            label="nic"
            type="text"
            name="nic"
            onChangeHandler={onChangeHandler}
            errors={errors.nic}
          />
          <InputGroup
            label="phone"
            type="text"
            name="phone"
            onChangeHandler={onChangeHandler}
            errors={errors.phone}
          />
          <InputGroup
            label="email"
            type="text"
            name="email"
            onChangeHandler={onChangeHandler}
            errors={errors.email}
          />
          <InputGroup
            label="check_in"
            type="text"
            name="check_in"
            onChangeHandler={onChangeHandler}
            errors={errors.check_in}
          />

           <InputGroup
            label="check_out"
            type="text"
            name="check_out"
            onChangeHandler={onChangeHandler}
            errors={errors.check_out}
          />
          <InputGroup
            label="no_of_rooms"
            type="text"
            name="no_of_rooms"
            onChangeHandler={onChangeHandler}
            errors={errors.no_of_rooms}
          />
          <InputGroup
            label="totalmember"
            type="text"
            name="totalmember"
            onChangeHandler={onChangeHandler}
            errors={errors.totalmember}
          />
          <InputGroup
            label="payment"
            type="text"
            name="payment"
            onChangeHandler={onChangeHandler}
            errors={errors.payment}
      
          
          />
            
          


            <button className="btn btn-primary" type="submit">Paynow</button>
         </form>
     </div>
     <div className="col-12 col-lg-7">
        <table className="table">
            <thead>
              <tr>
                <th scope='col'>fullname</th>
                <th scope='col'>nic</th>
                <th scope='col'>phone</th>
                <th scope='col'>email</th>
                <th scope='col'>check_in</th>
                <th scope='col'>check_out</th>
                <th scope='col'>no_of_rooms</th>
                <th scope='col'>totalmember</th>
                <th scope='col'>payment</th>
                <th scope='col'>Actions</th>
                
              </tr>
            </thead>
            <tbody>
  
              {rooms.map(({fullname,nic,phone,email,check_in,check_out,no_of_rooms,totalmember,payment, _id})=>(
                <RowDetails 
                 fullname={fullname} 
                  nic={nic}  
                  phone={phone} 
                  email={email} 
                  check_in={check_in}
                   check_out={check_out} 
                   no_of_rooms={no_of_rooms} 
                   totalmember={totalmember} 
                   payment={payment} 
                   Id={_id} 
                      OnDelete={OnDelete}

                    />
              ))}
        
                
                
            </tbody>
        </table>    
     </div>
    </div>
     </div>
  );
}

export default Rooms;
