import React, { useEffect, useState } from 'react';
import InputGroup from '../components/InputGroup';

import axios from 'axios';





function  payment() {
  
  const[setpayment] =useState([]);
  const [form ,setForm]=useState({});
  const[errors ,setErrors] =useState({});
  const [ setMessage]= useState("");
  const [ setShow] = useState(false);
  
  const onChangeHandler= (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
    
  };
  
 
 const onSbmitHandler= (e)=>{
  e.preventDefault();
  axios.post('api/payment', form)
  .then(res=>{
    setMessage(res.data.message)
    setShow(true)
    setTimeout(() => {
      setShow(false)
    }, 4000);
  })
   .catch(err=>setErrors(err.response.data))
   
 }

  useEffect(()=>{
     axios.get('api/payment')
    .then(res=>{
        setpayment(res.data)
  });
  },[]) 
  return (

    <div>
       <div className="row p-4">

     <div className="mt-4">
        <h2>ROOMS BOOKING</h2>
     </div>
     <div className="col-12 col-lg-4">
         <form onSubmit={onSbmitHandler}>

         <InputGroup
            label="fullname"
            type="text"
            name="fullname"
            onChangeHandler={onChangeHandler}
          
           
          
          />
          <InputGroup
            label="nic"
            type="number"
            name="nic"
            onChangeHandler={onChangeHandler}
           
          />
          <InputGroup
            label="phone"
            type="number"
            name="phone"
        
            
            onChangeHandler={onChangeHandler}
            
          
          />
          <InputGroup
            label="email"
            type="text"
            name="email"
            onChangeHandler={onChangeHandler}
            errors={errors.email}
          />
          <InputGroup
            label="check_in"
            type="date"
            name="check_in"
            onChangeHandler={onChangeHandler}
            errors={errors.check_in}
          />

           <InputGroup
            label="check_out"
            type="date"
            name="check_out"
            onChangeHandler={onChangeHandler}
            
          />
          <InputGroup
            label="no_of_rooms"
            type="number"
            name="no_of_rooms"
            id="quantity" 
            min="1"
            max="5"
            onChangeHandler={onChangeHandler}
            
          />
          <InputGroup
            label="totalmember"
            type="number"
            name="totalmember"
            id="quantity" 
            min="1"
            max="5"
            onChangeHandler={onChangeHandler}
            
          />
          <InputGroup
            label="payment"
            type="number"
            name="payment"
            onChangeHandler={onChangeHandler}
            
      
          
          />
            
          


            < button className="btn btn-primary"  type="submit"><a href='Payment'>Paynow</a></button>
         </form>
     </div>
     <div className="col-12 col-lg-7">
        <table className="table">
           
        </table>    
     </div>
    </div>
     </div>
  );
}

export default payment;
