import React, { useEffect } from 'react';
import InputGroup from '../components/InputGroup';
import RowDetails from '../components/RowDetails';
import './Rooms.css';
const axios = require('axios');



function Rooms() {
    useEffect(() => {
        // declare the async data fetching function
        const fetchData = async () => {
          // get the data from the api
          const data = await fetch('http://localhost:3700/api/rooms');
          // convert data to json
          const json = await data.json();
          return json;
        }
      
       
        const result = fetchData()
          .catch(console.error);;
      
       
        setData(result);
      }, [])
  return (
    <div>
       <div className="row p-4">
     <div className="mt-4">
        <h2>ROOMS BOOKING</h2>
     </div>
     <div className="col-12 col-lg-4">
         <form>
            
           <InputGroup lable="fullname" type="text" name="fullname"/>
           <InputGroup lable="nic" type="text" name="nic"/>
           <InputGroup lable="phone" type="text" name="phone"/>
           <InputGroup lable="email" type="text" name="email"/>
           <InputGroup lable="check_in" type="text" name="check_in"/>
           <InputGroup lable="check_out" type="text" name="check_out"/>
           <InputGroup lable="no_of_rooms" type="text" name="no_of_rooms"/>
           <InputGroup lable="totalmember" type="text" name="totalmember"/>
           <InputGroup lable="payment" type="text" name="payment"/> 
           
            <button className="btn btn-primary">Paynow</button>
         </form>
     </div>
     <div className="col-12 col-lg-7">
        <table className="table">
            <thead>
              <tr>
                <th scope='col'>fullname</th>
                <th scope='col'>nic</th>
                <th scope='col'>phone</th>
                <th scope='col'>email</th>
                <th scope='col'>check_in</th>
                <th scope='col'>check_out</th>
                <th scope='col'>no_of_rooms</th>
                <th scope='col'>totalmember</th>
                <th scope='col'> payment</th>
                <th scope='col'>Actions</th>
                
              </tr>
            </thead>
            <tbody>
                <RowDetails/>
            </tbody>
        </table>    
     </div>
    </div>
     </div>
  );
}

export default Rooms;
