import React from 'react'
import InputGroup from '../components/InputGroup'
function Details() {

    const [form ,setForm]=useState({});
    const[errors ,setErrors] =useState({});
    const [message, setMessage]= useState("");
    const [show , setShow] = useState(false);
  
    const onChangeHandler= (e) => {
      setForm({
        ...form,
        [e.target.name]: e.target.value,
      });
      
    };
   const onSbmitHandler= (e)=>{
    e.preventDefault();
    axios.post('api/rooms', form)
    .then(res=>{
      setMessage(res.data.message)
      setShow(true)
      setTimeout(() => {
        setShow(false)
      }, 4000);
    })
     .catch(err=>setErrors(err.response.data))
     
   }
  return (
    <div className="col-12 col-lg-4">
         <form onSubmit={onSbmitHandler}>

         <InputGroup
            label="fullname"
            type="text"
            name="fullname"
            onChangeHandler={onChangeHandler}
            errors={errors.fullname}
          
          />
          <InputGroup
            label="nic"
            type="text"
            name="nic"
            onChangeHandler={onChangeHandler}
            errors={errors.nic}
          />
          <InputGroup
            label="phone"
            type="text"
            name="phone"
            onChangeHandler={onChangeHandler}
            errors={errors.phone}
          />
          <InputGroup
            label="email"
            type="text"
            name="email"
            onChangeHandler={onChangeHandler}
            errors={errors.email}
          />
          <InputGroup
            label="check_in"
            type="text"
            name="check_in"
            onChangeHandler={onChangeHandler}
            errors={errors.check_in}
          />

           <InputGroup
            label="check_out"
            type="text"
            name="check_out"
            onChangeHandler={onChangeHandler}
            errors={errors.check_out}
          />
          <InputGroup
            label="no_of_rooms"
            type="text"
            name="no_of_rooms"
            onChangeHandler={onChangeHandler}
            errors={errors.no_of_rooms}
          />
          <InputGroup
            label="totalmember"
            type="text"
            name="totalmember"
            onChangeHandler={onChangeHandler}
            errors={errors.totalmember}
          />
          <InputGroup
            label="payment"
            type="text"
            name="payment"
            onChangeHandler={onChangeHandler}
            errors={errors.payment}
      
          
          />
            
          


            <button className="btn btn-primary" type="submit">Paynow</button>
         </form>
     </div>
  )
}

export default Details
