import React, { useEffect, useState } from 'react';

import axios from 'axios';





function Inventory() {
  const[inventory,setinventory] =useState([]);
  const [form ,setForm]=useState({});
  const[errors ,setErrors] =useState({});
  const [message, setMessage]= useState("");
  const [show , setShow] = useState(false);

  const onChangeHandler= (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
    
  };
 const onSbmitHandler= (e)=>{
  e.preventDefault();
  axios.post('api/inventory', form)
  .then(res=>{
    setMessage(res.data.message)
    setShow(true)
    setTimeout(() => {
      setShow(false)
    }, 4000);
  })
   .catch(err=>setErrors(err.response.data))
   
 }
 const OnDelete = (id__)=>{
  if(window.confirm("are you sure to delete this rooms")){

    axios.delete(`/api/inventory/${id__}`)
   .then(res=>{
    setMessage(res.data.message)
    setShow(true)
    setTimeout(() => {
      setShow(false)
    }, 4000);
     
   })
  }
}
  useEffect(()=>{
     axios.get('api/inventory')
    .then(res=>{
      setinventory(res.data)
  });
  },[]) 
  return (

    <div>
       <div className="row p-4">
       <Alert message={message} show={show} />
     <div className="mt-4">
        <h2>Inv</h2>
     </div>
     <div className="col-12 col-lg-4">
         <form  onSubmit={onSbmitHandler} >

         <InputGroup
            label="inventoryid"
            type="text"
            name="inventoryid"
            onChangeHandler={onChangeHandler}
            errors={errors.inventoryid}
         
          
          />
          <InputGroup
            label="inventoryname"
            type="text"
            name="inventoryname"
            onChangeHandler={onChangeHandler}
            errors={errors.inventoryname}
          
          />
          <InputGroup
            label="supplierid"
            type="text"
            name="supplierid"
            onChangeHandler={onChangeHandler}
            errors={errors.supplierid}
       
          />
          <InputGroup
            label="supplierdate"
            type="text"
            name="supplierdate"
            onChangeHandler={onChangeHandler}
            errors={errors.supplierdate}
         
          />
          <InputGroup
            label="quantity"
            type="text"
            name="quantity"
            onChangeHandler={onChangeHandler}
            errors={errors.quantity}
        
          />

           <InputGroup
            label="price"
            type="text"
            name="price"
            onChangeHandler={onChangeHandler}
            errors={errors.price}
         
          />
          <InputGroup
            label="totalamount"
            type="text"
            name="totalamount"
            onChangeHandler={onChangeHandler}
            errors={errors.totalamount}
         
          />
          <InputGroup
            label="image"
            type="text"
            name="image"
            onChangeHandler={onChangeHandler}
            errors={errors.image}
          
          />
          
            <button className="btn btn-primary" type="submit">AddToInventory</button>
         </form>
     </div>
     <div className="col-12 col-lg-7">
        <table className="table">
            <thead>
              <tr>
              <th scope='col'>inventoryid</th>
                <th scope='col'>inventoryname</th>
                <th scope='col'>supplierid</th>
                <th scope='col'>supplierdate</th>
                <th scope='col'>quantity</th>
                <th scope='col'>price</th>
                <th scope='col'>totalamount</th>
                <th scope='col'>image</th>
                <th scope='col'>Actions</th>

                
              </tr>
            </thead>
            <tbody>

            {inventory.map(({inventoryid,inventoryname,supplierid,supplierdate,quantity,price,totalamount,image, _id})=>(
                <RowDetails 
                inventoryid={inventoryid} 
                inventoryname={inventoryname}  
                supplierid={supplierid} 
                supplierdate={supplierdate} 
                quantity={quantity}
                price={price} 
                totalamount={totalamount} 
                image={image} 
                   
                   Id={_id} 
                   OnDelete={OnDelete}                 

                    />
              ))}
        
                
                
            </tbody>
        </table>    
     </div>
    </div>
     </div>
  );
}

export default Inventory;
