import React, { useEffect, useState } from 'react';
import InputGroup from '../components/InputGroup';
import RowDetails from '../components/RowDetails';
import axios from 'axios';



function Rooms() {
  const[rooms,setrooms] =useState([])
  const [form ,setForm]=useState({})

  const onChangeHandler= (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
    console.log(form);
    
  };


  useEffect(()=>{
     axios.get('api/rooms')
    .then(res=>{
      setrooms(res.data)
  })
  }, []) 
  return (
    <div>
       <div className="row p-4">

     <div className="mt-4">
        <h2>ROOMS BOOKING</h2>
     </div>
     <div className="col-12 col-lg-4">
         <form>

         <InputGroup
            label="fullname"
            type="text"
            name="fullname"
            onChangeHandler={onChangeHandler}
          
          />
          <InputGroup
            label="nic"
            type="text"
            name="nic"
            onChangeHandler={onChangeHandler}
          
          />
          <InputGroup
            label="phone"
            type="text"
            name="phone"
            onChangeHandler={onChangeHandler}
          
          />
          <InputGroup
            label="email"
            type="text"
            name="email"
            onChangeHandler={onChangeHandler}
          
          />
          <InputGroup
            label="check_in"
            type="text"
            name="check_in"
            onChangeHandler={onChangeHandler}
          
          />

           <InputGroup
            label="check_out"
            type="text"
            name="check_out"
            onChangeHandler={onChangeHandler}
          
          />
          <InputGroup
            label="no_of_rooms"
            type="text"
            name="no_of_rooms"
            onChangeHandler={onChangeHandler}
          
          />
          <InputGroup
            label="totalmember"
            type="text"
            name="totalmember"
            onChangeHandler={onChangeHandler}
          
          />
          <InputGroup
            label="payment"
            type="text"
            name="payment"
            onChangeHandler={onChangeHandler}
          
      
          
          />
            
          


            <button className="btn btn-primary">Paynow</button>
         </form>
     </div>
     <div className="col-12 col-lg-7">
        <table className="table">
            <thead>
              <tr>
                <th scope='col'>fullname</th>
                <th scope='col'>nic</th>
                <th scope='col'>phone</th>
                <th scope='col'>email</th>
                <th scope='col'>check_in</th>
                <th scope='col'>check_out</th>
                <th scope='col'>no_of_rooms</th>
                <th scope='col'>totalmember</th>
                <th scope='col'>payment</th>
                <th scope='col'>Actions</th>
                
              </tr>
            </thead>
            <tbody>
              {
               rooms.map(({fullname,nic,phone,email,check_in,check_out,no_of_rooms,totalmember,payment,_id})=>{
                <RowDetails  fullname={fullname}  nic={nic}  phone={phone} email={email} check_in={check_in} check_out={check_out} no_of_rooms={no_of_rooms} totalmember={totalmember} payment={payment} Id={_id}  />
               })
              }
          <RowDetails/>
                
                
            </tbody>
        </table>    
     </div>
    </div>
     </div>
  );
}

export default Rooms;
