import React from 'react'
import InputGroup from '../components/InputGroup'
import {  useEffect, useState } from 'react';
import axios from 'axios'
import { useParams } from 'react-router-dom';
function Details() {
    const[rooms,setrooms] =useState([]);
    const [form ,setForm]=useState({});
    const[errors ,setErrors] =useState({});
    const {id} = useParams();
    const onChangeHandler= (e) => {
      setForm({
        ...form,
        [e.target.name]: e.target.value,
      });
      
    };
   const onSbmitHandler= (e)=>{
    e.preventDefault();
    axios.put(`/api/rooms/${id}`, form)
    .then(res=>{
      
    })
     .catch(err=>setErrors(err.response.data))
     
   }
   useEffect(()=>{
    axios.get(`/api/rooms/${id}`)
   .then(res=>{
     setrooms(res.data)
 });
 },[]) 
  return (
    <div className="container mt-4 col-12 col-lg-4">
         <form onSubmit={onSbmitHandler}>

         <InputGroup
            label="fullname"
            type="text"
            name="fullname"
            onChangeHandler={onChangeHandler}
            errors={errors.fullname}
            value={rooms.fullname}
          
          />
          <InputGroup
            label="nic"
            type="text"
            name="nic"
            onChangeHandler={onChangeHandler}
            errors={errors.nic}
            value={rooms.nic}
          />
          <InputGroup
            label="phone"
            type="text"
            name="phone"
            onChangeHandler={onChangeHandler}
            errors={errors.phone}
            value={rooms.phone}
          />
          <InputGroup
            label="email"
            type="text"
            name="email"
            onChangeHandler={onChangeHandler}
            errors={errors.email}
            value={rooms.email}
          />
          <InputGroup
            label="check_in"
            type="text"
            name="check_in"
            onChangeHandler={onChangeHandler}
            errors={errors.check_in}
            value={rooms.check_in}
          />

           <InputGroup
            label="check_out"
            type="text"
            name="check_out"
            onChangeHandler={onChangeHandler}
            errors={errors.check_out}
            value={rooms.check_out}
          />
          <InputGroup
            label="no_of_rooms"
            type="text"
            name="no_of_rooms"
            onChangeHandler={onChangeHandler}
            errors={errors.no_of_rooms}
            value={rooms.no_of_rooms}
          />
          <InputGroup
            label="totalmember"
            type="text"
            name="totalmember"
            onChangeHandler={onChangeHandler}
            errors={errors.totalmember}
            value={rooms.totalmember}
          />
          <InputGroup
            label="payment"
            type="text"
            name="payment"
            onChangeHandler={onChangeHandler}
            errors={errors.payment}
            value={rooms.payment}
      
          
          />
            
          


            <button className="btn btn-primary" type="submit">Paynow</button>
         </form>
     </div>
  )
}

export default Details
