import React, { Component } from 'react';
import jsPDF from 'jspdf';

class Pdf extends Component {
    pdfGenerate=()=>{
        var doc=new jsPDF('landscape','px','a4','false');
        doc.text(60,60,'fullname:Rimas')
        doc.text(60,80,'nic')
        doc.text(60,100,'phone')
        doc.text(60,120,'check_in')
        doc.text(60,140,'check_out')
        doc.text(60,160,'no_of_rooms:045')
        doc.text(60,180,' totalmember')
        doc.text(60,200,' payment')
       doc.addPage()
        doc.text(60,220,' payment')

       

        
        

        doc.save('a.pdf')
    }
    
    render() {
        return (
            <div>
                <div style={{backroundcolor:'red'}}>
              <u><center> <h1>Rooms Details Reports</h1></center></u> 
                <div style={{textAlign:'left'}}><button class="btn-hover color-9" onClick={this.pdfGenerate}> PDF </button></div>
 
                </div>
                
            </div>
        );
    }
}

export default Pdf;
